INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Preheat the oven to 180C/350F/Gas 4. Grease and line the base of a 23cm/9in springform cake tin.', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Place the biscuits in a plastic bag and crush with a rolling pin to fine crumbs.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Melt the butter and add the biscuit crumbs, stir to combine. Place in the base of the cake tin and spread in an even layer, then flatten (use your fingers or a the back of a spoon).', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Bake in the oven for 10 minutes. Remove and leave to cool while you prepare the filling. ', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Reduce the oven to 160C/315F/Gas 2. ', 5);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'In a large bowl, beat together the cream cheese and sugar until smooth then add the sour cream and flour and beat again. ', 6);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Gradually add the eggs and vanilla extract beating well between each addition. (Beat in the eggs rather than whisk as you don''t want to incorporate too much air as this will affect the smooth surface of the cheesecake).', 7);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Pour the cream cheese mix on to the biscuit base then bake in the oven for 45 minutes. The cheesecake should be just set with a slight wobble and should still be cream on top with just a slight golden hint around the edges.', 8);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Once the cheesecake is cooked, turn off the oven and prop open the door so that it is slightly ajar and leave the cheesecake to cool in the oven – this prevents the top from cracking.', 9);
INSERT INTO Step (recipe_id, name, sequence) VALUES (17, 'Once the oven is cool you can remove the cheesecake to cool completely before removing from the tin.', 10);
