INSERT INTO Step (recipe_id, name, sequence) VALUES (20, 'Cook the pasta in a large pan of boiling salted water for 10-12 minutes, until al dente. Meanwhile, put the cream in a saucepan with the butter and a little grated nutmeg and warm through gently. Stir in the Parmesan.', 1);
