INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'For the filling, melt the butter over a low heat in a small saucepan. Once melted, remove from the heat and stir in all of the remaining filling ingredients until well combined. Set aside to cool. ', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Preheat the oven to 220C/425F/Gas 7. Grease a baking tray with the margarine.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'For the pastry, roll out the pastry on a lightly floured work surface to a thickness of about 3mm/⅛in. Using a 6cm/2½in cutter, cut the pastry into rounds.', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Place a teaspoon of the filling in the middle of each round, then brush the edges of half the pastry with milk. Bring the other half of the pastry over and seal. Bring the corners of the pastry up into the middle and pinch to seal. ', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Turn thr sealed pastry parcel over, so that the seam is underneath, then gently roll out until it is about ½cm/¼in thick. Gently pat back into a round shape and place onto the greased baking tray.', 5);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Slash each cake across three times using the tip of a sharp knife. Brush the cakes with milk and sprinkle with caster sugar. ', 6);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Bake for 15 minutes, or until the pastry is golden-brown and puffed up. Transfer the cakes to a wire rack to cool. ', 7);
INSERT INTO Step (recipe_id, name, sequence) VALUES (31, 'Dust the eccles cakes with icing sugar before serving. ', 8);
