INSERT INTO Step (recipe_id, name, sequence) VALUES (36, 'Preheat the oven to 180C/350F/Gas 4 and lin 2 x 12-hole fairy cake tins with paper cases.', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (36, 'In a large bowl, cream together the butter and sugar using an electric mixer until the mixture is pale and fluffy.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (36, 'Beat in the eggs one at a time, mixing until the egg is completely incorporated into the batter before adding the next. Add a tablespoon of flour if the mixture curdles.Mix in the vanilla extract.', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (36, 'Fold in the flour using a large metal spoon until no traces of flour are visible. Gently fold in the milk to loosen the mixture.', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (36, 'Spoon the mixture into the prepared tins so that they are ⅔ full. Bake the cakes for 15-20 minutes, or until golden-brown on top and springy to the touch. Set aside to cool for 5 minutes on a wire rack before removing from the tin.', 5);
