INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'For the lamb, heat a frying pan until hot, then add the oil and butter and allow the butter to foam.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'Add the lamb and fry for 4-5 minutes, turning occasionally, until golden-brown all over.', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'Transfer the lamb onto a wire rack in a roasting tin. Season well with salt and freshly ground black pepper and place into the oven to roast for 15-20 minutes for pink lamb, or for up to 30 minutes if you prefer your lamb medium to well done.', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'Remove the lamb from the oven and keep warm for five minutes to allow the meat to rest before serving. ', 5);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'For the ratatouille, place all of the vegetables, except the tomatoes and basil, into a bowl and toss together with the olive oil. Season with salt and freshly ground black pepper and place onto a clean roasting tray. Transfer to the oven and roast for 10-12 minutes, until just beginning to turn golden-brown.', 6);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'Add the tomatoes and basil, stir well, and return the ratatouille to the oven to cook for a further 20 minutes, or until the vegetables are just tender. Season, to taste, with salt and freshly ground black pepper and serve either hot or cold.', 7);
INSERT INTO Step (recipe_id, name, sequence) VALUES (52, 'To serve, spoon some ratatouille into the centre of each plate. Carve the lamb into thick slices and arrange across the ratatouille and serve.', 8);
