INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'For the crêpes, whisk the flour, sugar, salt, eggs and a third of the milk in a bowl to form a smooth batter.', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'Gradually stir in the rest of the the milk and all of the cream. Leave the batter to rest in a warm place for about an hour.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'Just before cooking, stir in the orange flower water, if using.', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'Brush a small 18–20cm/7–8in frying pan with a little of the clarified butter and place over a medium heat. Add a ladleful of the batter and tilt the pan to cover the base thinly; cook the crêpe for a minute on each side.', 4);
