INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'For the orange butter sauce (if using), strain the orange juice through a sieve into a saucepan and add the icing sugar. Gradually bring the mixture to the boil and cook until the volume of liquid has reduced by half.', 6);
INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'Turn off the heat and whisk in the butter, a little at a time.', 7);
INSERT INTO Step (recipe_id, name, sequence) VALUES (69, 'To serve, scatter the orange segments over each crêpe and roll up. Spoon over the orange sauce and garnish with a sprig of mint.', 8);
