INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Pre-heat oven 150C/300F/Gas 2.  Warm the ramekins in the oven, so they are warm when the caramel is poured in.', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'First make the caramel. Pour the sugar and six tablespoons of water into a clean stainless steel pan.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Dissolve the sugar slowly, stirring with a wooden spoon over a low heat.  ', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'When there are no sugar granules left, stop stirring and boil until the sugar turns a dark copper colour. ', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Remove immediately from the heat to ensure the caramel does not burn.  Quickly pour the caramel into the warmed ramekins.  ', 5);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Set aside to cool and become hard.  (Do not put in the fridge because the sugar will absorb moisture and go soft and tacky).', 6);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Once hard, butter the sides of the ramekins above the level of the caramel.', 7);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'For the custard, whisk the eggs, vanilla extract and caster sugar together in a bowl until well mixed.', 8);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Pour the milk into a saucepan, gently heat over a low heat until you can still just dip your finger in for a moment, then strain the milk through a fine sieve onto the egg mixture in the bowl.  ', 9);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Whisk together until smooth, then pour the mixture into the prepared ramekins.', 10);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Stand the ramekins in a roasting tin and fill the tin half-way with boiling water from a kettle.  ', 11);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Cook in the oven for about 20-30 minutes or until the custard has set.  Do not overcook the custard – check around the edges of the dishes, to make sure no bubbles are appearing.', 12);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'Take the crème caramels out of the oven, remove the ramekins from the tray and set on a cooling rack.  When cool, transfer to the fridge overnight so that the caramel is absorbed into the custard.', 13);
INSERT INTO Step (recipe_id, name, sequence) VALUES (82, 'To serve, loosen the sides of the custard by tipping the ramekin and loosen with a small palette knife round the edges.  Place a serving dish on top of the ramekin and turn upside down.  Serve with pouring cream.', 14);
