INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'For the jerk marinade, blend all of the jerk marinade ingredients in a food processor until they form a smooth paste. ', 1);
INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'For the chicken wings, place the chicken wings into an ovenproof dish and pour over the jerk marinade, turning the wings to coat them in the marinade. Cover the dish and chill in the fridge for at least four hours, or ideally overnight, turning the wings over every 2-3 hours.', 2);
INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'Half an hour before you want to eat, light the barbecue. (Alternatively, preheat the grill to 190C/375F/Gas 5.) When the flames have died down and the coals have turned ash-white, place the wings onto the grill and cook for 12-15 minutes, turning them over every so often and basting with the remaining jerk marinade, until the wings are golden-brown all over and are completely cooked through (the chicken wings are cooked through when the juices run clear when pierced in the thickest part using a skewer).', 3);
INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'Meanwhile, place the whole chillies onto the grill. Barbecue for 3-4 minutes, turning occasionally, or until golden-brown grill marks appear on all sides. Remove from the grill and keep warm.', 4);
INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'Meanwhile, for the sugared oranges, sprinkle the sugar onto a plate. Dredge the cut sides of each orange quarter in the sugar, then place the orange quarters, cut-sides down, onto the grill. Grill for 3-4 minutes, or until the sugar melts and turns golden-brown. ', 5);
INSERT INTO Step (recipe_id, name, sequence) VALUES (99, 'To serve, place three jerk chicken wings onto each of four serving plates. Place two sugared orange quarters and one grilled chilli alongside each serving of chicken wings.', 6);
